unsigned char hexToDec(string& hex)
{
	unsigned char leftByte = 0;
	unsigned char rightByte = 0;
	if (hex[0] >= '0' && hex[0] <= '9') {
		leftByte = hex[0] - '0';
	}
	else {
		leftByte = hex[0] - 87;
	}
	if (hex[1] >= '0' && hex[1] <= '9') {
		rightByte = hex[1] - '0';
	}
	else {
		rightByte = hex[1] - 87;
	}
	return leftByte * 16 + rightByte;
}

